"use strict";(function(){var CONTRIBUTORS_URL="https://api.github.com/repos/HyperiumClient/Hyperium/contributors";var sumContributions=function sumContributions(contributors){return contributors.map(function(c){return c.contributions}).reduce(function(p,n){return p+n})};fetchJson(CONTRIBUTORS_URL,function(data,error){var contributors=[];if(error){var cachedArray=localStorage.getItem("contributors");if(cachedArray&&cachedArray.length>0){contributors=JSON.parse(cachedArray)}}else{var arithmeticAverage=sumContributions(data)/data.length;var minimumContributions=arithmeticAverage*0.8;contributors=data.filter(function(contributor){return contributor.contributions>=minimumContributions}).map(function(contributor){return{name:contributor.login,profileUrl:contributor.html_url,avatarUrl:contributor.avatar_url,contributions:contributor.contributions}})}localStorage.setItem("contributors",JSON.stringify(contributors));var containerElement=document.getElementById("contributors-container");contributors.forEach(function(contributor){var element=document.createElement("div");element.classList.add("mdc-layout-grid__cell");element.classList.add("mdc-layout-grid__cell--span-4-phone");element.classList.add("mdc-layout-grid__cell--span-4-tablet");element.classList.add("mdc-layout-grid__cell--span-2-desktop");element.innerHTML="<div class=\"mdc-card\"><div class=\"mdc-card__media mdc-card__media--square\" style=\"background-image: url('".concat(contributor.avatarUrl,"');\"></div><div class=\"hyperium-card__primary\"><h2 class=\"hyperium-card__title mdc-typography--headline6\">").concat(contributor.name,"</h2><h3 class=\"hyperium-card__subtitle mdc-typography--subtitle2\">").concat(contributor.contributions," contributions</h3></div><div class=\"mdc-card__actions mdc-card__actions--full-bleed\">\n      <a class=\"mdc-button mdc-card__action mdc-card__action--button\" href=\"").concat(contributor.profileUrl,"\"><span class=\"mdc-button__label\">GitHub</span></a></div></div>");containerElement.appendChild(element)})})})();